class Metronome
{
    canvas = null;
    canvas2dContext = null;

    bpm = 120;
    delayMilliseconds = 0;

    ticking = false;

    x = 0;
    y = 0;

    count = 0;
    max = 4;

    x = 0;

    xOffset = 20;
    xSpacing = 75;

    y = 0;

    yOffset = 30;
    ySpacing = 20;

    barHeight = 90;
    barWidth = 30;

    beepSound = null;

    noise = true;

    constructor(canv)
    {
        this.canvas = canv;

        if (this.canvas != null)
        {
            this.canvas2dContext = this.canvas.getContext("2d");
        }

        this.beepSound = new Audio("data:audio/wav;base64,");  

        this.setDelay();
    }

    beep() 
    {
        this.beepSound.play();
    }

    setBPM(beat)
    {
        this.bpm = beat;
        this.setDelay();
    }

    getBPM()
    {
        return this.bpm;
    }

    setDelay()
    {
        this.delayMilliseconds = (1 / this.bpm) * 60 * 1000;
    }

    getDelay()
    {
        return this.delayMilliseconds();
    }

    async startTicking()
    {
        this.ticking = true;

        await this.tick();
    }

    turnOnNoise()
    {
        this.noise = true;
    }

    turnOffNoise()
    {
        this.noise = false;
    }

    stopTicking()
    {
        this.ticking = false;
        this.canvas2dContext.clearRect(0, 0, this.canvas.width, this.canvas.height);
        this.reset();
    }

    async tick()
    {
        var ticker = this;

        if (ticker.ticking == true)
        {
            if (ticker.noise)
            {
                ticker.beep();
            }

            if (ticker.canvas2dContext != null)
            {
                ticker.count++;
                ticker.draw();
            }

            window.setTimeout(function() {
                ticker.tick();
            }, ticker.delayMilliseconds);
        }
    }

    reset()
    {
        this.count = 0;
        this.x = 0;
        this.y = 0;
    }

    draw()
    {
        if (this.count == 0)
        {
        }
        else if (this.count <= this.max)
        {
            var randColor = "#" + this.randomColor();

            this.canvas2dContext.fillStyle = randColor;
            this.canvas2dContext.fillRect(this.xSpacing * this.x + this.xOffset, this.y * this.ySpacing + this.yOffset, this.barWidth, this.barHeight);

            this.x++;

            if (this.xSpacing * this.x + this.xOffset >= this.canvas.width)
            {
                this.y++;
                this.x = 0;
            }
        }
        else if (this.count > this.max)
        {
            this.canvas2dContext.clearRect(0, 0, this.canvas.width, this.canvas.height);
            this.count = 0;
            this.x = 0;
            this.y = 0;
        }
    }

    randomColor()
    {
        return Math.floor(Math.random()*16777215).toString(16);
    }
}